query.sparql:
BASE <http://www.semantic-systems-biology.org/>  
PREFIX skos: <http://www.w3.org/2004/02/skos/core#> 
PREFIX inheres_in: <http://purl.obolibrary.org/obo/RO_0000052>  
PREFIX molecularly_controls: <http://purl.obolibrary.org/obo/RO_0002448>
PREFIX proteinUri: <@proteinUri>
PREFIX graph1: <refseq>  
PREFIX graph2: <refprot> 
PREFIX graph3: <tf-tg> 

PREFIX taxon: <http://purl.obolibrary.org/obo/NCBITaxon_9606>  
SELECT DISTINCT ?geneUri ?gene ?protein
WHERE {  
	GRAPH graph3: {  
		?triple rdf:subject proteinUri: .  
		?triple rdf:predicate molecularly_controls: .  
		?triple rdf:object ?geneUri .  
  }  
	GRAPH graph2: {  
		proteinUri: skos:prefLabel ?protein .  
		proteinUri: inheres_in: taxon: . 
  }  
	GRAPH graph1: {  
		?geneUri skos:prefLabel ?gene .  
		?geneUri inheres_in: taxon: .  
  }  
}

		BASE <http://www.semantic-systems-biology.org/>  
		PREFIX skos: <http://www.w3.org/2004/02/skos/core#> 
		PREFIX inheres_in: <http://purl.obolibrary.org/obo/RO_0000052>  
		PREFIX molecularly_controls: <http://purl.obolibrary.org/obo/RO_0002448>
		PREFIX geneUri: <@geneUri>
		PREFIX graph1: <refseq>  
		PREFIX graph2: <refprot> 
		PREFIX graph3: <tf-tg> 
		PREFIX taxon: <@taxon>  
		SELECT DISTINCT ?proteinUri ?protein ?gene
		WHERE {  
			GRAPH graph3: {  
				?triple rdf:subject ?proteinUri .  
				?triple rdf:predicate molecularly_controls: .  
				?triple rdf:object geneUri: .  
		  }  
			GRAPH graph2: {  
				?proteinUri skos:prefLabel ?protein .  
  @limitToTaxon ?proteinUri inheres_in: taxon: . 
		  }  
			GRAPH graph1: {  
				geneUri: skos:prefLabel ?gene .  
  @limitToTaxon geneUri: inheres_in: taxon: .  
		  }  
		}

BASE <http://www.semantic-systems-biology.org/> PREFIX skos: <http://www.w3.org/2004/02/skos/core#> PREFIX inheres_in: <http://purl.obolibrary.org/obo/RO_0000052> PREFIX involved_in: <http://purl.obolibrary.org/obo/RO_0002331> PREFIX molecularly_controls: <http://purl.obolibrary.org/obo/RO_0002448> PREFIX graph1: <refseq> PREFIX graph2: <refprot> PREFIX graph3: <tf-tg> # Adjust the prefixes below accordingly PREFIX taxon: <@taxon> SELECT DISTINCT @returnFields WHERE { # Adjust the search patterns below accordingly FILTER regex ( str ( ?diseaseDescription ) , '@diseaseName', 'i' ) . GRAPH graph3: { ?triple rdf:subject ?proteinUri . ?triple rdf:predicate molecularly_controls: . ?triple rdf:object ?geneUri . }
GRAPH graph2: { ?proteinUri skos:prefLabel ?protein . ?proteinUri inheres_in: taxon: . ?proteinUri involved_in: ?omim . ?omim skos:definition ?diseaseDescription . ?omim skos:prefLabel ?disease . }
GRAPH graph1: { ?geneUri skos:prefLabel ?gene . ?geneUri inheres_in: taxon: . } } ORDER BY ?protein

BASE <http://www.semantic-systems-biology.org/>
            PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
            PREFIX involved_in: <http://purl.obolibrary.org/obo/RO_0002331>
            PREFIX graph1: <goa>
            PREFIX graph2: <refprot>
            # Adjust the prefixes below accordingly
            PREFIX protein: <@proteinID>
            SELECT ?query ?target ?goTerm
            WHERE {
            GRAPH graph1: {
            ?GOA_triple1 rdf:subject protein: .
            ?GOA_triple1 rdf:predicate involved_in: .
            ?GOA_triple1 rdf:object ?objectUri .
            ?GOA_triple2 rdf:subject ?protein2 .
            ?GOA_triple2 rdf:predicate involved_in: .
            ?GOA_triple2 rdf:object ?objectUri .
            }
            GRAPH graph2: {
            protein: skos:prefLabel ?query .
            ?protein2 skos:prefLabel ?target .
            }
            GRAPH <go-basic-inf> {
            ?objectUri skos:prefLabel ?goTerm .
            }
            }

BASE <http://www.semantic-systems-biology.org/>
            PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
            PREFIX inheres_in: <http://purl.obolibrary.org/obo/RO_0000052>
            PREFIX has_agent: <http://semanticscience.org/resource/SIO_000139>
            PREFIX graph: <refprot>
            # Adjust the prefixes below accordingly
            PREFIX taxon: <@taxon>
            SELECT ?proteinUri ?protein
            WHERE {
            GRAPH graph: {
            ?proteinUri inheres_in: taxon: .
            ?proteinUri skos:prefLabel ?protein .
            }
            GRAPH <intact> {
            ?ppi_id has_agent: ?proteinUri .
            }
            }
